import React from "react";
import { useContext, useEffect, useState } from "react";
import { View, StyleSheet, Text } from "react-native";

export default function TextItem(props) {
  return (
    <View style={styles.tile}>
      <Text style={styles.fontBigBold}>Seller: Tim Johnson</Text>
      <View style={styles.spaceBetween}>
        <Text style={styles.fontNormal}>
          Hours of Netflix watched this week
        </Text>
        <Text style={styles.price}>$0.1</Text>
      </View>
      <br></br>
      {/* Input answer */}
      <View style={styles.spaceBetween}>
        <View style={styles.inputAnswer}>
          <Text style={styles.fontNormal}>{props.text1}</Text>
        </View>
        {/* Buy button */}
        <View style={styles.buyButton}>
          <Text style={styles.fontNormalWhite}>Buy</Text>
        </View>
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  tile: {
    flexDirection: "column",
    alignItems: "flex-start",
    justifyContent: "center",
    width: "100%",
    backgroundColor: "#F4F7FB",
    borderRadius: 10,
    borderWidth: 1,
    padding: 20,
    marginBottom: 20,
  },

  spaceBetween: {
    flexDirection: "row",
    alignItems: "flex-start",
    justifyContent: "space-between",
    width: "100%",
  },

  fontBigBold: {
    fontSize: 18,
    fontWeight: "bold",
  },

  fontNormal: {
    fontSize: 15,
  },

  price: {
    fontSize: 15,
    marginRight: 15,
  },

  fontNormalWhite: {
    fontSize: 15,
    color: "white",
    fontWeight: "bold",
  },

  inputAnswer: {
    alignItems: "flex-start",
    justifyContent: "center",
    width: "79%",
    backgroundColor: "white",
    borderRadius: 12,
    borderWidth: 1,
    height: 40,
    paddingLeft: 10,
  },

  buyButton: {
    alignItems: "center",
    justifyContent: "center",
    width: "19%",
    height: 40,
    backgroundColor: "#3B5B81",
    borderRadius: 12,
  },
});
