import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  TextInput,
} from "react-native";

export default function Profile({ navigation }) {
  return (
    <View style={styles.container}>
      <View style={styles.profileSection}>
        {/* Profile section */}
        <Image
          style={styles.profilePicture}
          source={require("../assets/pictures/profilepic.jpg")}
        ></Image>
        <Text style={styles.profileName}>Tim Johnson</Text>

        {/* Profile information */}
        <View style={{ flexDirection: "column", alignItems: "flex-start" }}>
          <View style={{ flexDirection: "row" }}>
            <Text style={styles.fontBold}>Data sold: </Text>
            <Text style={styles.fontNormal}>1,232</Text>
          </View>
          <View style={{ flexDirection: "row" }}>
            <Text style={styles.fontBold}>User status: </Text>
            <Text style={styles.fontNormal}>Premium</Text>
          </View>
          <Text style={styles.fontBold}>Achievements:</Text>
          <Text style={styles.fontNormal}>
            ðŸ”¶ Top-seller{"\n"}ðŸ”· Data expert{"\n"}ðŸ”´ Money runner{"\n"}ðŸ”µ
            Baller
          </Text>
        </View>
        <Text style={styles.fontHeaderBold}>Balance:</Text>
        <Text style={styles.fontHeaderNormal}>$240.55</Text>
        <TouchableOpacity
          style={styles.dataValButton}
          onPress={() => navigation.navigate("Stats")}
        >
          <Text style={styles.fontBoldWhite}>Data value</Text>
        </TouchableOpacity>
      </View>

      {/* Data section */}
      <View style={styles.dataSection}>
        <Text style={styles.headLine}>Market</Text>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
          }}
        >
          <Text style={styles.underHeadline}>Data activity</Text>
          <Text style={styles.underHeadline}>Price</Text>
        </View>
        <TextInput style={styles.inputSection}></TextInput>
      </View>

      {/* Trending section */}
      <View style={styles.trendingSection}></View>

      {/* <form>
        <label>
          Religion: <br></br>
          <textarea type="text" name="name" />
        </label>
        <input type="submit" value="Submit" />
      </form> 
      <Button onPress={() => navigation.navigate("Market")}></Button> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "row",
    backgroundColor: "#FFFFFF",
    width: "100%",
    height: "100%",
  },

  //Profile section
  profileSection: {
    flex: 4,
    width: "100%",
    height: "100%",
    backgroundColor: "#E0EEFF",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",
  },

  profilePicture: {
    marginTop: 30,
    marginBottom: 10,
    resizeMode: "contain",
    width: 160,
    height: 160,
    borderRadius: 80,
  },

  profileName: {
    fontWeight: "bold",
    fontSize: 25,
    marginBottom: 50,
  },

  fontHeaderBold: {
    fontWeight: "bold",
    fontSize: 22,
    marginTop: 50,
  },

  fontHeaderNormal: {
    fontSize: 22,
  },

  fontBold: {
    fontSize: 15,
    fontWeight: "bold",
    marginBottom: 10,
  },

  fontNormal: {
    fontSize: 15,
  },

  fontBoldWhite: {
    fontSize: 15,
    fontWeight: "bold",
    color: "white",
  },

  dataValButton: {
    marginTop: 50,
    width: 150,
    height: 50,
    backgroundColor: "#3B5B81",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 20,
  },

  //Data section
  dataSection: {
    flex: 12,
    width: "100%",
    height: "100%",
    marginLeft: 20,
    backgroundColor: "#FFFFFF",
    flexDirection: "column",
    padding: 40,
  },

  headLine: {
    fontSize: 30,
    fontWeight: "bold",
    marginBottom: 20,
  },

  underHeadline: {
    fontSize: 22,
    fontWeight: "bold",
    marginBottom: 20,
  },

  inputSection: {
    width: "80%",
    height: 50,
    borderWidth: 1,
    borderRadius: 15,
  },

  //Trending section
  trendingSection: {
    flex: 9,
    marginRight: 30,
    marginLeft: 30,
    width: "100%",
    height: "90%",
    backgroundColor: "#EFF5FD",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",
    padding: 20,
    borderRadius: 20,
  },
});
