import React from "react";
import { useContext, useEffect, useState } from "react";
import { View, StyleSheet, Text } from "react-native";

export default function TextItem(props) {
  return (
    <View style={styles.tile}>
      <Text style={styles.fontBigBold}>Seller: Tim Johnson</Text>
      <Text style={styles.fontNormal}>Hours of Netflix watched this week</Text>

      {/* Input answer */}
      <View style={styles.inputAnswer}>
        <Text style={styles.fontNormal}>{props.text1}</Text>
      </View>
      {/* Buy button */}
      <View style={styles.buyButton}>
        <Text style={styles.fontNormalWhite}>Buy</Text>
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  tile: {
    flexDirection: "column",
    alignItems: "flex-start",
    justifyContent: "center",
    width: "100%",
    backgroundColor: "#F4F7FB",
    borderRadius: 10,
    borderWidth: 1,
    padding: 20,
  },

  fontBigBold: {
    fontSize: 15,
    fontWeight: "bold",
  },

  fontNormal: {
    fontSize: 12,
  },

  fontNormalWhite: {
    fontSize: 12,
    color: "white",
    fontWeight: "bold",
  },

  inputAnswer: {
    alignItems: "center",
    justifyContent: "center",
    width: "80%",
    height: 40,
    backgroundColor: "white",
    borderRadius: 12,
    borderWidth: 0.5,
  },

  buyButton: {
    alignItems: "center",
    justifyContent: "center",
    width: "20%",
    height: 40,
    backgroundColor: "#3B5B81",
    borderRadius: 12,
    borderWidth: 0.5,
  },
});
